// Desestructuración
// Asignación Desestructurante

const persona = {
    nombre: 'Tony',
    edad: 45,
    clave: 'Ironman',
    rango: 'Soldado'
};

// const { edad, clave, nombre, } = persona;

// console.log( nombre );
// console.log( edad );
// console.log( clave );

const useContext = ({ clave, nombre, edad, rango = 'Capitán',}) => {
    
    // console.log( nombre, edad, rango );

    return {
        nombreClave: clave,
        anios: edad
    }

}

const avenger = useContext( persona );

console.log( avenger );
