import getHeroeById from './bases/08-imp-exp';

const promesa = new Promise( (resolve, reject) => {
    setTimeout( () => {
        // Tarea
        // Importar
        const p1 = getHeroeById(2);
        reject( p1 );
        reject( 'No se pudo encontrar el héroe' );
    }, 2000)
});

promesa.then( (heroe) => {
    console.log( 'Heroe', heroe )
})
.catch( err => console.warn( err ));
