// Tarea
// 1. El primer valor del arr se llamara nombre
// 2. El segundo valor del arr se llamara setNombre

const useState = ( valor ) => {
    return [ valor, () => { console.log( 'Hola Mundo' ) } ];
}

const [ nombre, setNombre ] = useState( 'Goku' );

console.log( nombre );
setNombre();
